        setTimeout(() => {
          const newRecommendation = {
            id: Date.now(),
            agency: ['ÏÜåÎ∞©Ï≤≠', 'ÏÇ∞Î¶ºÏ≤≠', 'ÏßÄÏûêÏ≤¥'][Math.floor(Math.random() * 3)],
            title: [
              'Í∏¥Í∏â ÌôîÏû¨ ÏßÑÏïï Î∞è Íµ¨Ï°∞ ÌôúÎèô Í∂åÍ≥†Ïïà',
