// frontend/src/components/Layout/Layout.tsx
import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Layout.css';

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const location = useLocation();

  const menuItems = [
    { path: '/dashboard', label: 'ÎåÄÏãúÎ≥¥Îìú', icon: 'ÔøΩÔøΩ' },
    { path: '/ai-recommendation', label: 'AI Í∂åÍ≥†Ïïà', icon: 'ÔøΩÔøΩ' },
    { path: '/approval', label: 'ÏäπÏù∏ ÌîÑÎ°úÏÑ∏Ïä§', icon: '‚úÖ' },
    { path: '/log-audit', label: 'Î°úÍ∑∏ Î∞è Í∞êÏÇ¨', icon: 'üìã' }
  ];

  return (
    <div className="layout">
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <h2>ÏÇ∞Î∂à ÎåÄÏùë AI</h2>
          <button 
            className="sidebar-toggle"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            {sidebarOpen ? '‚óÄ' : '‚ñ∂'}
          </button>
        </div>
        <nav className="sidebar-nav">
          {menuItems.map(item => (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
            >
              <span className="nav-icon">{item.icon}</span>
              {sidebarOpen && <span className="nav-label">{item.label}</span>}
            </Link>
          ))}
        </nav>
      </div>
      
      <div className="main-content">
        <header className="header">
          <div className="header-left">
            <h1>ÎØø:Ïùå 2.0 LLM AI Agent</h1>
          </div>
          <div className="header-right">
            <div className="user-info">
              <span className="user-name">Í¥ÄÎ¶¨Ïûê</span>
              <div className="user-avatar">üë§</div>
            </div>
          </div>
        </header>
        
        <main className="content">
          {children}
        </main>
      </div>
    </div>
  );
};

export default Layout;
